package positive;

public class PositiveNegativeZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		if(a>0) {
			System.out.println("POSITIVE NUMBER");
		}else if(a<0) {
			System.out.println("NEGATIVE NUMBER");
			
		}else {
		System.out.println("IT IS ZERO");
	}}

}
